mapper.select:
SELECT boardNum, subject, content, cb.created, cb.saveFileName, cb.originalFileName, cb.memberNum, m.name memberName, clubNum, categoryNum
FROM clubBoard cb JOIN member m ON cb.memberNum=m.memberNum
WHERE clubNum=#{clubNum} AND categoryNum=#{categoryNum}

mapper.update:
UPDATE clubBoard 
SET subject=#{subject}, content=#{content}, saveFileName=#{saveFileName, jdbcType=VARCHAR}, originalFileName=#{originalFileName, jdbcType=VARCHAR} 
WHERE boardNum=#{boardNum}
