mapper.select:
SELECT * FROM(
	SELECT ROWNUM rnum, tb.* FROM(
		SELECT num, notice, n.memberNum, subject, n.name, n.created, hitCount
			FROM notice n
			LEFT OUTER JOIN member m ON n.memberNum = m.memberNum
		
			
				
			
		
		ORDER BY notice DESC, num DESC
	) tb WHERE ROWNUM <= #{end}
)WHERE rnum >= ${start}
